import { useEffect, useState } from 'react';
import './App.css';
import {LoginSocialFacebook} from 'reactjs-social-login'
import {FacebookLoginButton} from 'react-social-login-buttons'
import axios from 'axios';

function App() {

  const [shortaccess, setshortaccess] = useState(null);
  const [longaccess, setlongaccess] = useState(null);

  // window.FB.login(function(response){
  //   // handle the response 
  //   console.log(response)
  // });

  // setTimeout(() => {
  //   window.FB.logout(function(response){
  //     // handle the response 
  //     console.log("logged out" , response)
  //   });
  // }, 10000);

  useEffect(() => {

    axios.get(`https://graph.facebook.com/oauth/access_token?grant_type=fb_exchange_token&client_id=837259694145774&client_secret=64206b60353f794f73b7dee570ee499f&fb_exchange_token=${shortaccess}`).then((data)=>{
      console.log("long user access token  = ", data)
      setlongaccess(data.data.access_token)
    }).catch((error)=>{
      console.log("error_long_access_token", error)
    })

  }, [shortaccess])

  useEffect(() => {

    console.log("shortaccess = ", shortaccess)
    axios.get(`https://graph.facebook.com/PAGE-ID?
    fields=access_token&
    access_token=${shortaccess}`,{headers: { "Content-Type": "application/json", Authorization: "Bearer ", }}).then((data)=>{
      console.log("page access token  = ", data)
    }).catch((error)=>{
      console.log("error_page_access_token", error)
    })

  }, [shortaccess])


  return (
    <>
    <h1>idris bohra </h1>


    <LoginSocialFacebook
    appId='837259694145774'
    onResolve={(data)=>{
      console.log("data = ", data);
      setshortaccess(data.data.accessToken);
    }}
    onReject={(error)=>{
      console.log(error)
    }}
    ><div style={{width:"200px"}}><FacebookLoginButton/></div></LoginSocialFacebook>


    </>
  );
}

export default App;
